#!/bin/bash

#Read Name to display on welcome screen 
read -p "Enter Name to Display on welcome note: " welcome_name
read -p "Enter the Logged in | Message " Log_message

sed -i '2 i\echo **************************************************************************************************************************************************************** \necho **************************************************************************************************************************************************************** \n \n \n \n \n \n \n \necho Welcome back ~~~~ ------- \"\$1\" -------- ~~~~ \n \necho "Date: $(date +%A)" \n \necho "Time: $(date "+%K:%M")" \n \ncowsay -f bud-frogs " $(fortune -s) " \n \ncowsay -f tux " <<<<< LOGGED IN | \"\$Log_message\" >>>>>  " \n \n \n \n \n \n \n \necho **************************************************************************************************************************************************************** \necho ****************************************************************************************************************************************************************' ~/.bashrc

echo "Done Changing .bashrc --- Login Screen Friendly Created"

